export default {
    name: "HomeMapLocation",
    components: {
        HomeMapTooltip,
    },
    setup() {
        const store = useStore();

        const tooltip = reactive({
            label: "",
            x: 0,
            y: 0,
        });

        const isTooltip = ref(false);

        const group = computed(() => {
            return store.getters["getGroup"];
        });

        const map = computed(() => {
            return store.state.map;
        });

        const handleSetCountry = ({clientX, clientY}, country) => {
            store.commit("setCountry", country);

            tooltip.label = COUNTRIES[country];
            tooltip.x = clientX;
            tooltip.y = clientY;

            isTooltip.value = true;
        };

        const handleClearCountry = () => {
            store.commit("setCountry", null);

            isTooltip.value = false;
        };

        const handleCountryIsActive = (country) => {
            if (map.value.length) {
                return map.value.find((findCountry) => findCountry === country)
                    ? "home-map-location__country--active"
                    : "";
            }

            if (group.value) {
                return group.value.map.find(
                    (findCountry) => findCountry === country
                )
                    ? "home-map-location__country--active"
                    : "";
            }

            return "";
        };

        return {
            tooltip,
            isTooltip,
            handleSetCountry,
            handleClearCountry,
            handleCountryIsActive,
        };
    },
};
